mapper.select:
select

from user_message_t
where conversation_id = #{conversationId,jdbcType=VARCHAR}
limit #{offset,jdbcType=INTEGER} , #{limit,jdbcType=INTEGER}

select
, count(id) as count
from user_message_t
where user_id = #{userId}
OR send_user_id = #{userId}  group by conversation_id order by create_time limit #{}
